
// Generated by Pvelte
const Counter = () => {
    const count = createState(0);  // States
    
function increment() {
    count.set(count.get() + 1);
}


function decrement() {
    count.set(count.get() - 1);
}
  // Functions
    
    return {
        mount: (target) => {
            const template = `<button data-onclick="increment">Increment</button>
<h2>The count is <span data-bind="count">${count.get()}</span></h2>
<button data-onclick="decrement">Decrement</button>`;
            target.innerHTML = template;
            
            // Setup event listeners
            
            const incrementButtons = target.querySelectorAll(`[data-onclick="increment"]`);
            incrementButtons.forEach(button => {
                button.addEventListener('click', increment);
            });
            

            const decrementButtons = target.querySelectorAll(`[data-onclick="decrement"]`);
            decrementButtons.forEach(button => {
                button.addEventListener('click', decrement);
            });
            
            
            // Setup state subscriptions
            
            count.subscribe(value => {
                target.querySelectorAll('[data-bind="count"]').forEach(el => {
                    el.textContent = value;
                });
            });
            
        }
    };
};
